import React, { useState } from "react";
import {
  Container,
  Box,
  Typography,
  TextField,
  Button,
  Paper,
} from "@mui/material";
import api from "../api/api"; // Import your api utility

const ForgotPassword = () => {
  const [email, setEmail] = useState("");
  const [sent, setSent] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post("/accounts/forgot-password", { email });
      setSent(true);
    } catch (err) {
      alert(err.response?.data?.error || "Error sending reset link");
    }
  };

  return (
    <Container maxWidth="sm">
      <Paper elevation={3} sx={{ p: 4, mt: 6 }}>
        <Typography variant="h5" align="center" gutterBottom>
          Forgot Password
        </Typography>
        {!sent ? (
          <Box component="form" onSubmit={handleSubmit}>
            <TextField
              label="Enter your email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              fullWidth
              margin="normal"
              required
            />
            <Button
              type="submit"
              variant="contained"
              color="primary"
              fullWidth
              sx={{ mt: 2 }}
            >
              Send Reset Link
            </Button>
          </Box>
        ) : (
          <Typography align="center" color="success.main" sx={{ mt: 2 }}>
            âœ… Reset link sent to your email!
          </Typography>
        )}
      </Paper>
    </Container>
  );
};

export default ForgotPassword;
