import React, { useState } from "react";
import {
  Container,
  Typography,
  Button,
  Box,
  Grid,
  Card,
  CardContent,
  CardActions,
  TextField,
  Paper,
  MenuItem,
} from "@mui/material";
import api from "../api/api"; // Import your api utility
import ResumeUpload from "../components/ResumeUpload";

const EmployeeDashboard = () => {
  const [skills, setSkills] = useState([]);
  const [courses, setCourses] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [filter, setFilter] = useState("all");
  const [location, setLocation] = useState("");
  const [radius, setRadius] = useState("10");

  // Handle analysis completion from ResumeUpload
  const handleAnalysisComplete = (data) => {
    setSkills(data.skills || []);
    setCourses(data.courses || []);
    setJobs(data.jobs || []);
  };

  // Filter courses
  const filteredCourses =
    filter === "all" ? courses : courses.filter((c) => c.type === filter);

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>
        Employee Dashboard
      </Typography>

      {/* Resume Upload Section */}
      <Paper sx={{ p: 3, mb: 4 }}>
        <ResumeUpload onAnalysisComplete={handleAnalysisComplete} />
      </Paper>

      {/* Skills Recommendation */}
      {skills.length > 0 && (
        <Paper sx={{ p: 3, mb: 4 }}>
          <Typography variant="h6">Recommended Skills to Improve</Typography>
          <ul>
            {skills.map((s, i) => (
              <li key={i}>{s}</li>
            ))}
          </ul>
        </Paper>
      )}

      {/* Course Feed */}
      {courses.length > 0 && (
        <Paper sx={{ p: 3, mb: 4 }}>
          <Typography variant="h6" gutterBottom>
            Recommended Courses
          </Typography>
          <TextField
            select
            label="Filter Courses"
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
            sx={{ mb: 2, width: "200px" }}
          >
            <MenuItem value="all">All</MenuItem>
            <MenuItem value="free">Free</MenuItem>
            <MenuItem value="paid">Paid</MenuItem>
          </TextField>
          <Grid container spacing={2}>
            {filteredCourses.map((course, i) => (
              <Grid item xs={12} sm={6} md={4} key={i}>
                <Card>
                  <CardContent>
                    <Typography variant="h6">{course.title}</Typography>
                    <Typography variant="body2" color="text.secondary">
                      {course.description}
                    </Typography>
                    <Typography variant="caption" display="block" sx={{ mt: 1 }}>
                      Type: {course.type} | Level: {course.level}
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <Button
                      size="small"
                      href={course.link}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      Visit Course
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Paper>
      )}

      {/* Hyper-local Job Recommendations */}
      <Paper sx={{ p: 3, mb: 4 }}>
        <Typography variant="h6" gutterBottom>
          Local Job Recommendations
        </Typography>
        <Box sx={{ display: "flex", gap: 2, mb: 2, alignItems: "center" }}>
          <TextField
            label="Location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            placeholder="Enter city or zip code"
          />
          <TextField
            label="Radius (km)"
            type="number"
            value={radius}
            onChange={(e) => setRadius(e.target.value)}
            sx={{ width: "120px" }}
          />
          <Button
            variant="contained"
            onClick={async () => {
              try {
                const res = await api.get(`/jobs/search?location=${location}&radius=${radius}`);
                setJobs(res.data || []);
              } catch (err) {
                alert(err.response?.data?.error || "Error fetching jobs");
              }
            }}
            disabled={!location}
          >
            Search Jobs
          </Button>
        </Box>
        <Grid container spacing={2}>
          {jobs.map((job, i) => (
            <Grid item xs={12} md={6} key={i}>
              <Card>
                <CardContent>
                  <Typography variant="h6">{job.title}</Typography>
                  <Typography color="text.secondary">{job.company}</Typography>
                  <Typography variant="body2">{job.location}</Typography>
                  <Typography variant="body2" sx={{ mt: 1 }}>
                    {job.description}
                  </Typography>
                  {job.salary && (
                    <Typography variant="subtitle2" sx={{ mt: 1 }}>
                      Salary: {job.salary}
                    </Typography>
                  )}
                </CardContent>
                <CardActions>
                  <Button
                    size="small"
                    href={job.applyLink || job.link}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    Apply Now
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
          {jobs.length === 0 && location && (
            <Grid item xs={12}>
              <Typography variant="body1" align="center" sx={{ py: 4 }}>
                No jobs found in this area. Try expanding your search radius.
              </Typography>
            </Grid>
          )}
        </Grid>
      </Paper>
    </Container>
  );
};

export default EmployeeDashboard;
