import React, { useState } from "react";
import {
  Box,
  TextField,
  Button,
  Typography,
  InputAdornment,
  Link
} from "@mui/material";
import EmailIcon from "@mui/icons-material/Email";
import LockIcon from "@mui/icons-material/Lock";
import loginImage from "../assets/image.png";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Email:", email);
    console.log("Password:", password);
  };

  return (
    <Box
      sx={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
        width: "100vw",
        bgcolor: "#E6E6FA",
      }}
    >
      <Box
        sx={{
          display: "flex",
          flexDirection: { xs: "column", sm: "row" },
          bgcolor: "white",
          borderRadius: 2,
          boxShadow: 4,
          overflow: "hidden",
          maxWidth: 700,
          width: "100%",
        }}
      >
        
        <Box
          component="img"
          src={loginImage}
          alt="Login Illustration"
          sx={{
            width: 300,
            objectFit: "cover",
            display: { xs: "none", sm: "block" },
          }}
        />

        
        <Box p={4} sx={{ flex: 1 }}>
          <Typography
            variant="h5"
            align="center"
            gutterBottom
            sx={{ color: "primary.main", fontWeight: "bold" }}
          >
            Login
          </Typography>
          <form onSubmit={handleSubmit}>
            <TextField
              label="Email"
              type="email"
              fullWidth
              margin="normal"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <EmailIcon color="primary" />
                  </InputAdornment>
                ),
              }}
            />
            <TextField
              label="Password"
              type="password"
              fullWidth
              margin="normal"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <LockIcon color="primary" />
                  </InputAdornment>
                ),
              }}
            />

            
            <Typography
              variant="body2"
              align="right"
              sx={{ mt: 1 }}
            >
              <Link href="/forgot-password" underline="hover" sx={{ fontWeight: "bold" }}>
                Forgot Password?
              </Link>
            </Typography>

            <Button
              type="submit"
              variant="contained"
              fullWidth
              sx={{ mt: 2 }}
            >
              Login
            </Button>

          
            <Typography
              variant="body2"
              align="center"
              sx={{ mt: 2 }}
            >
              New here?{" "}
              <Link href="/register" underline="hover" sx={{ fontWeight: "bold" }}>
                Register Now
              </Link>
            </Typography>
          </form>
        </Box>
      </Box>
    </Box>
  );
};

export default Login;
