import React, { useState, useEffect } from "react";
import {
  Container,
  Typography,
  Button,
  Box,
  Grid,
  Card,
  CardContent,
  CardActions,
  TextField,
  Paper,
  Tabs,
  Tab,
} from "@mui/material";
import axios from "axios";

const RecruiterDashboard = () => {
  const [jobs, setJobs] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [location, setLocation] = useState("");
  const [activeTab, setActiveTab] = useState(0);
  const [applicants, setApplicants] = useState({
    high: [],
    medium: [],
    low: [],
  });

  // post a new job
  const handlePostJob = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/recruiter/post-job`,
        { title, description, location }
      );
      setJobs([...jobs, res.data.job]);
      setTitle("");
      setDescription("");
      setLocation("");
    } catch (err) {
      alert(err.response?.data?.error || "Error posting job");
    }
  };

  // Ftch all posted jobs
  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const res = await axios.get(
          `${process.env.REACT_APP_API_URL}/recruiter/my-jobs`
        );
        setJobs(res.data.jobs || []);
      } catch (err) {
        console.error(err);
      }
    };
    fetchJobs();
  }, []);

  // fetch appplicants by category
  const fetchApplicants = async (jobId) => {
    try {
      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/recruiter/applicants/${jobId}`
      );
      setApplicants(res.data.applicants || { high: [], medium: [], low: [] });
    } catch (err) {
      alert(err.response?.data?.error || "Error fetching applicants");
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>
        Recruiter Dashboard
      </Typography>

      {/* Job Posting Section 
      <Paper sx={{ p: 3, mb: 4 }}>
        <Typography variant="h6">Post a New Job</Typography>
        <Box component="form" onSubmit={handlePostJob} sx={{ mt: 2 }}>
          <TextField
            fullWidth
            label="Job Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            sx={{ mb: 2 }}
          />
          <TextField
            fullWidth
            label="Description"
            multiline
            rows={3}
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            sx={{ mb: 2 }}
          />
          <TextField
            fullWidth
            label="Location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            sx={{ mb: 2 }}
          />
          <Button type="submit" variant="contained" color="primary">
            Post Job
          </Button>
        </Box>
      </Paper>

      {/* Job Listings */}
      <Paper sx={{ p: 3, mb: 4 }}>
        <Typography variant="h6">My Job Postings</Typography>
        <Grid container spacing={2} sx={{ mt: 2 }}>
          {jobs.map((job, i) => (
            <Grid item xs={12} md={6} key={i}>
              <Card>
                <CardContent>
                  <Typography variant="h6">{job.title}</Typography>
                  <Typography variant="body2">{job.description}</Typography>
                  <Typography color="text.secondary">
                    {job.location}
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button
                    size="small"
                    onClick={() => fetchApplicants(job._id)}
                  >
                    View Applicants
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Paper>

      {/* Applicant Categorization */}
      {applicants.high.length > 0 ||
      applicants.medium.length > 0 ||
      applicants.low.length > 0 ? (
        <Paper sx={{ p: 3, mb: 4 }}>
          <Typography variant="h6">Applicants</Typography>
          <Tabs
            value={activeTab}
            onChange={(e, newValue) => setActiveTab(newValue)}
            sx={{ mb: 2 }}
          >
            <Tab label={`High Fit (${applicants.high.length})`} />
            <Tab label={`Medium Fit (${applicants.medium.length})`} />
            <Tab label={`Low Fit (${applicants.low.length})`} />
          </Tabs>

          <Grid container spacing={2}>
            {activeTab === 0 &&
              applicants.high.map((app, i) => (
                <Grid item xs={12} md={6} key={i}>
                  <Card>
                    <CardContent>
                      <Typography variant="h6">{app.name}</Typography>
                      <Typography>Email: {app.email}</Typography>
                      <Typography>
                        Skills: {app.skills.join(", ")}
                      </Typography>
                    </CardContent>
                    <CardActions>
                      <Button size="small" href={app.resumeLink} target="_blank">
                        View Resume
                      </Button>
                    </CardActions>
                  </Card>
                </Grid>
              ))}

            {activeTab === 1 &&
              applicants.medium.map((app, i) => (
                <Grid item xs={12} md={6} key={i}>
                  <Card>
                    <CardContent>
                      <Typography variant="h6">{app.name}</Typography>
                      <Typography>Email: {app.email}</Typography>
                      <Typography>
                        Skills: {app.skills.join(", ")}
                      </Typography>
                    </CardContent>
                    <CardActions>
                      <Button size="small" href={app.resumeLink} target="_blank">
                        View Resume
                      </Button>
                    </CardActions>
                  </Card>
                </Grid>
              ))}

            {activeTab === 2 &&
              applicants.low.map((app, i) => (
                <Grid item xs={12} md={6} key={i}>
                  <Card>
                    <CardContent>
                      <Typography variant="h6">{app.name}</Typography>
                      <Typography>Email: {app.email}</Typography>
                      <Typography>
                        Skills: {app.skills.join(", ")}
                      </Typography>
                    </CardContent>
                    <CardActions>
                      <Button size="small" href={app.resumeLink} target="_blank">
                        View Resume
                      </Button>
                    </CardActions>
                  </Card>
                </Grid>
              ))}
          </Grid>
        </Paper>
      ) : null}
    </Container>
  );
};

export default RecruiterDashboard;
