import React, { useState, useEffect } from "react";
import { Navigate } from "react-router-dom";
import axios from "axios";

const PrivateRoute = ({ children, role }) => {
  const [isVerified, setIsVerified] = useState(null);
  const token = localStorage.getItem("token");
  const userRole = localStorage.getItem("role");

  useEffect(() => {
    // Check recruiter verification status if needed
    if (role === "recruiter" && token && userRole === "recruiter") {
      const checkVerification = async () => {
        try {
          const res = await axios.get(
            `${process.env.REACT_APP_API_URL}/recruiter/verification-status`,
            { headers: { Authorization: `Bearer ${token}` } }
          );
          setIsVerified(res.data.verified);
        } catch (err) {
          setIsVerified(false);
        }
      };
      checkVerification();
    }
  }, [role, token, userRole]);

  if (!token) {
    return <Navigate to="/login" replace />;
  }

  if (role && userRole !== role) {
    return <Navigate to="/unauthorized" replace />;
  }

  // For recruiters, check if they're verified
  if (role === "recruiter" && isVerified === false) {
    return <Navigate to="/recruiter-verification" replace />;
  }

  if (role === "recruiter" && isVerified === null) {
    return <div>Loading verification status...</div>;
  }

  return children;
};

export default PrivateRoute;
