import React, { useState } from "react";
import {
  Container,
  Typography,
  Button,
  Box,
  Grid,
  Card,
  CardContent,
  CardActions,
  TextField,
  Paper,
  MenuItem,
} from "@mui/material";
import axios from "axios";

const EmployeeDashboard = () => {
  const [resume, setResume] = useState(null);
  const [skills, setSkills] = useState([]);
  const [courses, setCourses] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [filter, setFilter] = useState("all");
  const [location, setLocation] = useState("");
  const [radius, setRadius] = useState("10");

  // Upload resume and analyze
  const handleResumeUpload = async (e) => {
    e.preventDefault();
    if (!resume) return alert("Please upload a resume");

    const formData = new FormData();
    formData.append("resume", resume);

    try {
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/employee/analyze-resume`,
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );

      setSkills(res.data.skills || []);
      setCourses(res.data.courses || []);
      setJobs(res.data.jobs || []);
    } catch (err) {
      alert(err.response?.data?.error || "Error analyzing resume");
    }
  };

  // Filter courses
  const filteredCourses =
    filter === "all" ? courses : courses.filter((c) => c.type === filter);

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>
        Employee Dashboard
      </Typography>

      {/* Resume Upload Section */}
      <Paper sx={{ p: 3, mb: 4 }}>
        <Typography variant="h6">Upload Resume for Analysis</Typography>
        <Box component="form" onSubmit={handleResumeUpload}>
          <input
            type="file"
            accept=".pdf,.doc,.docx"
            onChange={(e) => setResume(e.target.files[0])}
            style={{ marginTop: "10px" }}
          />
          <Button
            type="submit"
            variant="contained"
            color="primary"
            sx={{ ml: 2, mt: 1 }}
          >
            Analyze Resume
          </Button>
        </Box>
      </Paper>

      {/* Skills Recommendation */}
      {skills.length > 0 && (
        <Paper sx={{ p: 3, mb: 4 }}>
          <Typography variant="h6">Recommended Skills to Improve</Typography>
          <ul>
            {skills.map((s, i) => (
              <li key={i}>{s}</li>
            ))}
          </ul>
        </Paper>
      )}

      {/* Course Feed */}
      {courses.length > 0 && (
        <Paper sx={{ p: 3, mb: 4 }}>
          <Typography variant="h6" gutterBottom>
            Recommended Courses
          </Typography>
          <TextField
            select
            label="Filter Courses"
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
            sx={{ mb: 2, width: "200px" }}
          >
            <MenuItem value="all">All</MenuItem>
            <MenuItem value="free">Free</MenuItem>
            <MenuItem value="paid">Paid</MenuItem>
          </TextField>
          <Grid container spacing={2}>
            {filteredCourses.map((course, i) => (
              <Grid item xs={12} sm={6} md={4} key={i}>
                <Card>
                  <CardContent>
                    <Typography variant="h6">{course.title}</Typography>
                    <Typography variant="body2" color="text.secondary">
                      {course.description}
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <Button
                      size="small"
                      href={course.link}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      Visit
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Paper>
      )}

      {/* Hyper-local Job Recommendations */}
      <Paper sx={{ p: 3, mb: 4 }}>
        <Typography variant="h6" gutterBottom>
          Local Job Recommendations
        </Typography>
        <Box sx={{ display: "flex", gap: 2, mb: 2 }}>
          <TextField
            label="Location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
          />
          <TextField
            label="Radius (km)"
            type="number"
            value={radius}
            onChange={(e) => setRadius(e.target.value)}
          />
          <Button
            variant="outlined"
            onClick={async () => {
              try {
                const res = await axios.get(
                  `${process.env.REACT_APP_API_URL}/employee/jobs?location=${location}&radius=${radius}`
                );
                setJobs(res.data.jobs || []);
              } catch (err) {
                alert(err.response?.data?.error || "Error fetching jobs");
              }
            }}
          >
            Search Jobs
          </Button>
        </Box>
        <Grid container spacing={2}>
          {jobs.map((job, i) => (
            <Grid item xs={12} md={6} key={i}>
              <Card>
                <CardContent>
                  <Typography variant="h6">{job.title}</Typography>
                  <Typography>{job.company}</Typography>
                  <Typography variant="body2">{job.location}</Typography>
                </CardContent>
                <CardActions>
                  <Button
                    size="small"
                    href={job.link}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    Apply
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Paper>
    </Container>
  );
};

export default EmployeeDashboard;
