// src/App.js
import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { AppBar, Toolbar, Button, Typography, Container, Box } from "@mui/material";

// Sample pages
function Home() {
  return (
    <Box textAlign="center" mt={5}>
      <Typography variant="h3" gutterBottom>Home</Typography>
      <Typography variant="body1">Welcome to CareerCrafts ðŸš€</Typography>
    </Box>
  );
}

function About() {
  return (
    <Box textAlign="center" mt={5}>
      <Typography variant="h3" gutterBottom>About</Typography>
      <Typography variant="body1">This is an AI-powered career guidance platform.</Typography>
    </Box>
  );
}

function Contact() {
  return (
    <Box textAlign="center" mt={5}>
      <Typography variant="h3" gutterBottom>Contact</Typography>
      <Typography variant="body1">Reach us at support@careercrafts.com</Typography>
    </Box>
  );
}

function App() {
  return (
    <Router>
      {/* Navigation Bar */}
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            CareerCrafts
          </Typography>
          <Button color="inherit" component={Link} to="/">Home</Button>
          <Button color="inherit" component={Link} to="/about">About</Button>
          <Button color="inherit" component={Link} to="/contact">Contact</Button>
        </Toolbar>
      </AppBar>

      {/* Page Content */}
      <Container>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>
      </Container>
    </Router>
  );
}

export default App;
